import React, { useState, useEffect } from 'react'

import { AgGridReact } from 'ag-grid-react'
import 'ag-grid-community/dist/styles/ag-grid.css'
import 'ag-grid-community/dist/styles/ag-theme-material.css'



function Customers() {
    const [customers, setCustomers] = useState([])
    
    useEffect(() => {
        getCustomers()
    }, [])

    const getCustomers = () => {
        fetch('https://customerrest.herokuapp.com/api/customers')
        .then (response => response.json())
        .then(data => setCustomers(data.content))
        .catch(err => console.error(err))
    }

    const columns = [
        {field: 'firstname', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'lastname', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'streetaddress', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'postcode', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'city', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'email', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'phone', sortable: true, filter: true, suppressSizeToFit: true}
    ]

    return (
        <div>
            <div className = 'ag-theme-material' style = {{height: 720, width: '80%', margin: 'auto'}}>
                <AgGridReact
                    rowData = {customers}
                    columnDefs = {columns}
                    headerHeight = '20'
                    pagination = 'true'
                    paginationPageSize = '12'>
                </AgGridReact>
            </div>
        </div>
    )
}

export default Customers
