import React, { useState, useEffect } from 'react'

import { IconButton } from '@material-ui/core';
import DeleteOutlinedIcon from '@material-ui/icons/DeleteOutlined'
import EditOutlinedIcon from '@material-ui/icons/EditOutlined'
import AddCircleOutlineOutlinedIcon from '@material-ui/icons/AddCircleOutlineOutlined';
import DirectionsRunOutlinedIcon from '@material-ui/icons/DirectionsRunOutlined' // for show trainings

import { AgGridReact } from 'ag-grid-react'
import 'ag-grid-community/dist/styles/ag-grid.css'
import 'ag-grid-community/dist/styles/ag-theme-material.css'


function Customers() {
    const [customers, setCustomers] = useState([])
    
    useEffect(() => {
        getCustomers()
    }, [])

    const getCustomers = () => {
        fetch('https://customerrest.herokuapp.com/api/customers')
        .then (response => response.json())
        .then(data => setCustomers(data.content))
        .catch(err => console.error(err))
    }

    const deleteCustomer = (params) => {
        if (window.confirm('Delete customer?')) {
            fetch(params.value[0].href, {
                method: 'DELETE'
            })
            .then (_ => fetch(params.value[2].href, {
                method: 'DELETE'
            }))
            .then (_ => getCustomers())
            .catch(err => console.error(err))
        }
    }

    const columns = [
        {headerName: 'Last name', field: 'lastname', sortable: true, filter: true, suppressSizeToFit: true},
        {headerName: 'First Name', field: 'firstname', sortable: true, filter: true, suppressSizeToFit: true},
        {headerName: 'Address', field: 'streetaddress', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'postcode', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'city', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'email', sortable: true, filter: true, suppressSizeToFit: true},
        {field: 'phone', sortable: true, filter: true, suppressSizeToFit: true},
        {
            headerName: '',
            field: 'links',
            suppressSizeToFit: true,
            cellRendererFramework: params =>
                <IconButton color = 'secondary' onClick = {() => deleteCustomer(params)}>
                    <DeleteOutlinedIcon />
                </IconButton>
        }
    ]

    return (
        <div>
            <div>
                <h1>Customers</h1>
            </div>
            <div className = 'ag-theme-material' style = {{height: 600, width: '80%', margin: 'auto'}}>
                <AgGridReact
                    rowData = {customers}
                    columnDefs = {columns}
                    pagination = 'true'
                    paginationPageSize = '10'
                    resizable = 'true'
                    >
                </AgGridReact>
            </div>
        </div>
    )
}

export default Customers
