import React, { useState, useEffect } from 'react'

import { AgGridReact } from 'ag-grid-react'
import 'ag-grid-community/dist/styles/ag-grid.css'
import 'ag-grid-community/dist/styles/ag-theme-material.css'

const nameValueGetter = (params) => {
    return params.customer.lastname + ' ' + params.customer.firstname
}

function Trainings() {
    const [trainings, setTrainings] = useState([])
    
    useEffect(() => {
        getTrainings()
    }, [])

    const getTrainings = () => {
        fetch('https://customerrest.herokuapp.com/gettrainings')
        .then (response => response.json())
        .then(data => setTrainings(data))
        .catch(err => console.error(err))
    }

    const columns = [
        {field: 'date', sortable: true, filter: true},
        {field: 'activity', sortable: true, filter: true},
        {field: 'duration', sortable: true, filter: true, width: 100},
        {
            headerName: 'Customer',
            valueGetter: nameValueGetter,
            sortable: true, filter: true
        },
        ]

    return (
        <div>
            <div className = 'ag-theme-material' style = {{height: 720, width: '80%', margin: 'auto'}}>
                <AgGridReact
                    rowData = {trainings}
                    columnDefs = {columns}
                    pagination = 'true'
                    paginationPageSize = '12'>
                </AgGridReact>
            </div>
        </div>
    )
}

export default Trainings
