import React, { useState, useEffect } from 'react'

import { AgGridReact } from 'ag-grid-react'
import 'ag-grid-community/dist/styles/ag-grid.css'
import 'ag-grid-community/dist/styles/ag-theme-material.css'

import moment from 'moment'

function Trainings() {
    const [trainings, setTrainings] = useState([])
    
    useEffect(() => {
        getTrainings()
    }, [])

    const getTrainings = () => {
        fetch('https://customerrest.herokuapp.com/gettrainings')
        .then (response => response.json())
        .then(data => setTrainings(data))
        .catch(err => console.error(err))
    }

    const deleteTraining = (params) => {
        if (window.confirm('Delete training?')) {
            fetch('https://customerrest.herokuapp.com/trainings/' + params.value, {
                method: 'DELETE'
            })
            .then (_ => getTrainings())
            .catch(err => console.error(err))
        }
    }

    const columns = [
        {
            headerName: 'Date',
            valueGetter: params => {
                return moment(params.data.date).format("DD.MM.YYYY")
            },
            suppressSizeToFit: true,
            sortable: true,
            filter: true
        },
        {field: 'activity', suppressSizeToFit: true, sortable: true, filter: true},
        {field: 'duration', suppressSizeToFit: true, sortable: true, filter: true},
        {
            headerName: 'Customer', 
            valueGetter: params => {
                return params.data.customer.lastname + ' ' + params.data.customer.firstname
            },
            suppressSizeToFit: true,
            sortable: true,
            filter: true
        },
        {
            headerName: '',
            field: 'id',
            suppressSizeToFit: true,
            cellRendererFramework: params =>
                <IconButton color = 'secondary' onClick = {() => deleteTraining(params)}>
                    <DeleteOutlinedIcon />
                </IconButton>
        }
        ]

    return (
        <div>
            <div>
                <h1>Trainings</h1>
            </div>
            <div className = 'ag-theme-material' style = {{height: 600, width: '60%', margin: 'auto'}}>
                <AgGridReact
                    rowData = {trainings}
                    columnDefs = {columns}
                    pagination = 'true'
                    paginationPageSize = '10'
                    resizable = 'true'>
                </AgGridReact>
            </div>
        </div>
    )
}

export default Trainings
